    $fields = $fields[0];","                } else {","                    $name = array_shift($fields);","                    switch ($name) {","                        case 'SIZE':","                            $fields = ($fields ? $fields[0] : 0);","                            break;","                        case 'AUTH':","                            if (!is_array($fields)) {","                                $fields = [];","                            }","                            break;","                        default:","                            $fields = true;","                    }","                }","                $this-\u003eserver_caps[$name] = $fields;","            }","        }","    }","","    /**","     * Send an SMTP MAIL command.","     * Starts a mail transaction from the email address specified in","     * $from. Returns true if successful or false otherwise. If True","     * the mail transaction is started and then one or more recipient","     * commands may be called followed by a data command.","     * Implements RFC 821: MAIL \u003cSP\u003e FROM:\u003creverse-path\u003e \u003cCRLF\u003e and","     * two extensions, namely XVERP and SMTPUTF8.","     *","     * The server's EHLO response is not checked. If use of either","     * extensions is enabled even though the server does not support","     * that, mail submission will fail.","     *","     * XVERP is documented at https://www.postfix.org/VERP_README.html","     * and SMTPUTF8 is specified in RFC 6531.","     *","     * @param string $from Source address of this message","     *","     * @return bool","     */","    public function mail($from)","    {","        $useVerp = ($this-\u003edo_verp ? ' XVERP' : '');","        $useSmtputf8 = ($this-\u003edo_smtputf8 ? ' SMTPUTF8' : '');","","        return $this-\u003esendCommand(","            'MAIL FROM',","            'MAIL FROM:\u003c' . $from . '\u003e' . $useSmtputf8 . $useVerp,","            250","        );","    }","","    /**","     * Send an SMTP QUIT command.","     * Closes the socket if there is no error or the $close_on_error argument is true.","     * Implements from RFC 821: QUIT \u003cCRLF\u003e.","     *","     * @param bool $close_on_error Should the connection close if an error occurs?","     *","     * @return bool","     */","    public function quit($close_on_error = true)","    {","        $noerror = $this-\u003esendCommand('QUIT', 'QUIT', 221);","        $err = $this-\u003eerror; //Save any error","        if ($noerror || $close_on_error) {","            $this-\u003eclose();","            $this-\u003eerror = $err; //Restore any error from the quit command","        }","","        return $noerror;","    }","","    /**","     * Send an SMTP RCPT command.","     * Sets the TO argument to $toaddr.","     * Returns true if the recipient was accepted false if it was rejected.","     * Implements from RFC 821: RCPT \u003cSP\u003e TO:\u003cforward-path\u003e \u003cCRLF\u003e.","     *","     * @param string $address The address the message is being sent to","     * @param string $dsn     Comma separated list of DSN notifications. NEVER, SUCCESS, FAILURE","     *                        or DELAY. If you specify NEVER all other notifications are ignored.","     *","     * @return bool","     */","    public function recipient($address, $dsn = '')","    {","        if (empty($dsn)) {","            $rcpt = 'RCPT TO:\u003c' . $address . '\u003e';","        } else {","            $dsn = strtoupper($dsn);","            $notify = [];","","            if (strpos($dsn, 'NEVER') !== false) {","                $notify[] = 'NEVER';","            } else {","                foreach (['SUCCESS', 'FAILURE', 'DELAY'] as $value) {","                    if (strpos($dsn, $value) !== false) {","                        $notify[] = $value;","                    }","                }","            }","","            $rcpt = 'RCPT TO:\u003c' . $address . '\u003e NOTIFY=' . implode(',', $notify);","        }","","        return $this-\u003esendCommand(","            'RCPT TO',","            $rcpt,","            [250, 251]","        );","    }","","    /**","     * Send SMTP XCLIENT command to server and check its return code.","     *","     * @return bool True on success","     */","    public function xclient(array $vars)","    {","        $xclient_options = \"\";","        foreach ($vars as $key =\u003e $value) {","            if (in_array($key, SMTP::$xclient_allowed_attributes)) {","                $xclient_options .= \" {$key}={$value}\";","            }","        }","        if (!$xclient_options) {","            return true;","        }","        return $this-\u003esendCommand('XCLIENT', 'XCLIENT' . $xclient_options, 250);","    }","","    /**","     * Send an SMTP RSET command.","     * Abort any transaction that is currently in progress.","     * Implements RFC 821: RSET \u003cCRLF\u003e.","     *","     * @return bool True on success","     */","    public function reset()","    {","        return $this-\u003esendCommand('RSET', 'RSET', 250);","    }","","    /**","     * Send a command to an SMTP server and check its return code.","     *","     * @param string    $command       The command name - not sent to the server","     * @param string    $commandstring The actual command to send","     * @param int|array $expect        One or more expected integer success codes","     *","     * @return bool True on success","     */","    protected function sendCommand($command, $commandstring, $expect)","    {","        if (!$this-\u003econnected()) {","            $this-\u003esetError(\"Called $command without being connected\");","","            return false;","        }","        //Reject line breaks in all commands","        if ((strpos($commandstring, \"\\n\") !== false) || (strpos($commandstring, \"\\r\") !== false)) {","            $this-\u003esetError(\"Command '$command' contained line breaks\");","","            return false;","        }","        $t